using namespace std;
int map[205][205], dis[205][205],d[205][205], n,mins, dir[4][2] = {-1,0,0,1,1,0,0,-1};
bool flag[205][205];

#define M 1000000009
void bfs()
{
    queue<int> q;
    int i, x, y, x1, y1;
    x = 1; q.push(x);
    y = 1; q.push(y);
    dis[1][1] = map[1][1];
    while(!q.empty())
    {
        x = q.front(); q.pop();
        y = q.front(); q.pop();
        for(i = 0; i < 4; ++i)
        {
            x1 = x + dir[i][0];
            y1 = y + dir[i][1];
            if(map[x1][y1] && dis[x1][y1] > dis[x][y] + map[x1][y1])
            {
                dis[x1][y1] = dis[x][y] + map[x1][y1];
                q.push(x1); q.push(y1);
            }
        }
    }
    mins = dis[1][n];
    for(i=2; i<n; ++i)
        if(dis[i][n-i+1] < mins)
            mins = dis[i][n-i+1];
}

int dfs(int x,int y)
{
    int i,tx,ty;
    if(x==1 && y==1) return 1;
    if(flag[x][y]) return d[x][y];  
    flag[x][y]=true;

    d[x][y]=0;
    for(i=0;i<4;i++)
    {
        tx=x+dir[i][0];
        ty=y+dir[i][1];
        if(map[tx][ty] && dis[x][y] == dis[tx][ty] + map[x][y])   
            d[x][y]=(d[x][y]+dfs(tx,ty)) % M;
    }
    return d[x][y];
}

int main()
{
    int i,j;
    while(scanf("%d",&n)!=EOF)
    {
        if(n==0) break;
        memset(map,0,sizeof(map));
        for(i=1; i<=n; ++i)
            for(j=1; j<=n; ++j)
            {
                scanf("%d",&map[i][j]);
                dis[i][j] = M+2;
            }
        for(i=1; i<n; ++i)
            for(j=1; j<=n-i; ++j)
            {
                map[i][j] += map[n-j+1][n-i+1];
                map[n-j+1][n-i+1] = 0;
            }
        bfs();
        memset(flag,false,sizeof(flag));
        int key=0;                 
        for(i = 1; i <= n; i++)
           if(mins == dis[i][n-i+1])
                 key=(key+dfs(i,n-i+1)) % M;
        printf("%d\n",key);

    }
    return 0;
}

