using namespace std;
#define mid (L+R)/2
#define lson rt*2,L,mid
#define rson rt*2+1,mid+1,R
const int maxn=110000;
const int INF=1e9;
int minv[maxn*4];
int a[maxn],cnt=0;
int b[5000];
char str[100];
void PushUP(int rt){
        minv[rt]=min(minv[rt*2],minv[rt*2+1]);
}
void build(int rt,int L,int R){
    if(L==R){
        scanf("%d",&minv[rt]);
        a[cnt++]=minv[rt];
        return ;
    }
    build(lson);
    build(rson);
    PushUP(rt);
}
int get_b(int st,int en){
    int cnt=0;
    int tm,tmp=0;
    for(int i=st;i<en;i++){
        if(str[i]>='0'&&str[i]<='9'){
           tm=str[i]-'0';
           tmp*=10;
           tmp+=tm;
        }else{
            b[cnt++]=tmp;
            tmp=0;
        }
    }
    return cnt;
}
void exchange(int la,int ra){
    int tm=a[la];
    a[la]=a[ra];
    a[ra]=tm;
}
int query(int rt,int L,int R,int l_ran,int r_ran){
    if(l_ran<=L&&R<=r_ran){
        return minv[rt];
    }
    int ret_l=INF,ret_r=INF;
    if(l_ran<=mid){
        ret_l=query(lson,l_ran,r_ran);
    }
    if(r_ran>mid){
        ret_r=query(rson,l_ran,r_ran);
    }
    return min(ret_l,ret_r);
}
void update(int rt,int L,int R,int pos,int val){
    if(L==R){
        minv[rt]=val;
        return ;
    }
    if(pos<=mid){
        update(lson,pos,val);
    }else{
        update(rson,pos,val);
    }
    PushUP(rt);
}
