using namespace std;

int Eular(int n)
{
    int ans = n;
    for(int i = 2; i * i <= n; i++)
    {
        if(n % i == 0)      
        {
            ans -= ans / i;     
            while(n % i == 0)  
                n /= i;
            if(n == 1) 
                break;
        }
    }
    if(n != 1)  
        ans -= ans / n;
    return ans;
}
