#include<stdio.h>  
int Eular(int  n)  
{  
    int i;  
    int  ans=n;  
    for(i=2; i*i<=n; ++i)  
    {  
        if(n%i==0)  
        {  
            ans-=ans/i;  
            while(n%i==0)  
                n=n/i;  
            if(n==1)  
                break;  
        }  
    }  
    if(n!=1)  
        ans-=ans/n;  
    return ans;  
}  
