#define MAXN 30010
using namespace std;

struct ST
{
    int l,r,v;
};

ST st[MAXN*4];
int a[MAXN],b[MAXN];

int BSearch(int k,int high)
{
    int low,mid;
    for(low=0;low<=high;)
    {
        mid=(low+high)>>1;
        if(b[mid]==k)
            return mid;
        else if(b[mid]<k)
            low=mid+1;
        else
            high=mid-1;
    }
    return -1;
}

void BuildST(int l,int r,int i)
{
    st[i].l=l;
    st[i].r=r;
    st[i].v=0;
    int mid=(l+r)>>1;
    if(l<r)
    {
        BuildST(l,mid,i<<1);
        BuildST(mid+1,r,(i<<1)+1);    
    }
}

void Insert(int i,int k)
{
    int mid=(st[i].l+st[i].r)>>1;
    if(k<=mid)
    {
        st[i].v++;
        if(st[i].l<st[i].r)
            Insert(i<<1,k);
    }    
    else
        Insert((i<<1)+1,k);
}

int Query(int i,int j)
{
    if(st[i].l==st[i].r)
        return st[i].l;
    if(j<=st[i].v)
        return Query(i<<1,j);
    return Query((i<<1)+1,j-st[i].v);

}

