int n, c;
int pos[100005];

bool judge(int k)    
{
    int cnt = 1;
    int st = pos[0];
    for(int i = 1; i < n; ++i)
    {
        if(pos[i] - st >= k)
        {
            ++cnt;
            if(cnt >= c)
                return true; 
            st = pos[i];
        }
    }
    return false;
}

int binary_search()  
{
    int left = 0;
    int right = pos[n-1] - pos[0];
    int mid = (left + right) >> 1;
    while(left <= right)
    {
        if(judge(mid))  
            left = mid+1;
        else           
            right = mid-1;
        mid = (left + right) >> 1;
    }
    return left-1;
}

