int cmp(const void *a,const void *b)
{
    return (*(Node *)a).num - (*(Node *)b).num;
}

int lowbit(int i)
{
    return i&(-i);
}

void add(int i,int value)
{
    while(i<MAX)
    {
        c[i]+=value;
        i+=lowbit(i);
    }
}

int sum(int i)
{
    int t=0;
    while(i>0)
    {
        t+=c[i];
        i-=lowbit(i);
    }
    return t;
}

