long long  one(int n, int m,int  k)  
{  
    int a[4]={n,m,k};  
    int Max=-1000;  
    int Min=10000000;  
    for(int i=0;i<3;i++)  
    {  
        if(a[i]>Max)  
            Max=a[i];  
        if(a[i]<Min)  
            Min=a[i];  
    }  
    long long Mid=n+m+k-Max-Min;  
    long long  value=(long long )((Min-1)+(Min*(Mid-1))+(long long)(Min*Mid*(Max-1)));  
    return value;  
}  
