    for(int i=0;i<=18;i+=9)
      for(int j=0;j<9;j++)
      {
          if(tmp[i+j]>=3)
          {
              tmp[i+j]-=3;
              ret++;
          }
          while(j+2<9 && tmp[i+j] && tmp[i+j+1] &&tmp[i+j+2])
          {
              tmp[i+j]--;
              tmp[i+j+1]--;
              tmp[i+j+2]--;
              ret++;
          }
      }
    for(int j=0;j<7;j++)
    {
        if(tmp[27+j]>=3)
        {
            tmp[27+j]-=3;
            ret++;
        }
    }
    if(ret==4)return true;
    return false;
}

