int BFS(int pos, int tim)
{
    if(res[pos][tim] >= 0)
        return res[pos][tim];
    if(tim > maxt)
        return 0;
    int t1, t2, t3;
    t1 = t2 = t3 = 0;
    t1 = BFS(pos, tim+1)+pie[pos][tim+1];
    if(pos > 0)
        t2 = BFS(pos-1, tim+1)+pie[pos-1][tim+1];
    if(pos < 10)
        t3 = BFS(pos+1, tim+1)+pie[pos+1][tim+1];
    return res[pos][tim] = max(t1, max(t2, t3));
}

