#include <iostream>
#include <cstdio>
using namespace std;
const int maxn=1e5+10,inf=0x3f3f3f3f,mod=1000000007;
int x[maxn];
typedef long long LL;
LL get(LL n){
    LL ans=1;
    for(int i=0;i<n;i++){
        ans=ans*x[i]%mod;
    }
    return ans;
}
LL power(LL a,LL p){
    LL ans=1,temp=a;
    while(p){
        if(p&1) ans=ans*temp%mod;
        temp=temp*temp%mod;
        p>>=1;
    }
    return ans;
}
int main()
{
    int t,ca=1;
    cin>>t;
    while(t--){
        LL n,m;
        while(cin>>n>>m){
             int negsum=0,f1=-inf,dex;
             for(int i=0;i<n;i++){
                 scanf("%d",&x[i]);
                 if(x[i]<0) negsum++;
                 if(x[i]<0&&f1<x[i]){
                     dex=i;
                     f1=x[i];
                 }
             }
             if(negsum&1){
                 if(-f1>=m){
                     x[dex]+=m;
                     printf("Case %d: %lld\n",ca++,get(n));
                     continue;
                 }
                 m+=x[dex];
                 x[dex]=0;
             }
             for(int i=0;i<n&&m>0;i++){
                 if(x[i]==0){  x[i]++;  m--;  }
             }
             for(int i=0;i<n&&m>0;i++){
                 if(x[i]==1){ x[i]++; m--;  }
             }
             int minx=inf;
             dex=0;
             for(int i=0;i<n&&m>0;i++){
                 if(x[i]==2){
                    x[i]++;  m--;
                }
                if(minx>x[i]&&x[i]>=0){
                    minx=x[i];
                    dex=i;
                }
             }
             if(m==1){
                  x[dex]++;
                  printf("Case %d: %lld\n",ca++,get(n));
                  continue;
             }
             if(m==2){
                  printf("Case %d: %lld\n",ca++,get(n)*2%mod);
                  continue;
             }
             if(m%3==0){
                  printf("Case %d: %lld\n",ca++,get(n)*power(3,m/3)%mod);
                  continue;
             }
             if((m-1)%3==0){
                  printf("Case %d: %lld\n",ca++,get(n)*power(3,m/3-1)%mod*4%mod);
                  continue;
             }
             if((m-2)%3==0){
                  printf("Case %d: %lld\n",ca++,get(n)*power(3,m/3)%mod*2%mod);
             }
        }
    }
    return 0;
}

