#include <cstdio>
#include <iostream>
#include <algorithm>

using namespace std;

int len, n, m;
int pos[500005];

bool judge(int k)
{
    int st = 0;
    int cnt = 0;
    for(int i = 0; i <= n; ++i)
    {
        if(pos[i] - st <= k)
        {
            while(pos[i] - st <= k && i <= n)
                ++i;
            st = pos[--i];
            ++cnt;
        }    
    }
    if(cnt <= m)
        return true;
    return false;
}

int binary_search()
{

    int left = 0;
    int right = len;

    for(int i = 1; i <= n; ++i)
        if(pos[i] - pos[i-1] > left)
            left = pos[i] - pos[i-1];

    int mid = (left+right)>>1;
    while(left <= right)
    {
        if(judge(mid)) 
            right = mid-1;
        else         
            left = mid+1;
        mid = (left+right)>>1;
    }
    return right + 1; 
}

int main()
{
    while(~scanf("%d%d%d", &len, &n, &m))
    {
        for(int i = 0; i < n; ++i)
            scanf("%d", &pos[i]);
        pos[n] = len;
        sort(pos, pos+n);
        printf("%d\n", binary_search());
    }    
    return 0;
}

