bool search(int x,int *p,int len)  
{  
    int left=0,right=len-1;  
    while(left<=right)   
    {  
        int mid=(left+right)/2;  
        if(x>p[mid])  
            left=mid+1;  
        else if(x==p[mid])  
            {  
                return true;  
            }  
        else  
            right=mid-1;  
    }  
    return false;  
}  
