        dp[1][i] = 1;  
    for(i = 2; i < 11; i++)  
    {  
        for(j = 2; j < 67; j++)  
        {  
            for(k = 1; k < 7; k++)  
                if(k < j)  
                    dp[i][j] += dp[i - 1][j - k];  
        }  
    }  

    double sum;  
    for(i = 1; i < 11; i++)  
