using namespace std;  
int a[100001],b[100001];  
struct node {  
    int v; int n;  
    node(){}  
    node(int a,int b):v(a),n(b){}   
    bool operator < (const node& tmp ) const{  
    return v< tmp.v;   
    }  
};  
bool cmp(int aa,int bb)  
{  
    return aa > bb;  
}  
void fun(int *A,int *B,int *C,int n)  
{  
    priority_queue<node> q;  
    int i;  
    for( i = 0 ; i < n ; i++)  
        q.push(node(A[i]+B[0],0)); 
    for( i = 0 ; i < n ; i++)  
    {  
        node tmp = q.top(); q.pop(); 
        C[i] = tmp.v;          
        int b = tmp.n;              
        if(b<n-1)           
        {  
            q.push(node(tmp.v - B[b] +B[b+1],b+1));
        }  
    }  
}  
int main()  
{  
    int k,i,n;  
    while(scanf("%d%d",&n,&k)!=EOF)  
    {  
        for(i = 0 ; i < n ; i++)  
            scanf("%d",&a[i]);  
        for(i = 0 ; i < n ;i++)  
            scanf("%d",&b[i]);  
        sort(a,a+n,cmp);     
        sort(b,b+n,cmp);  
        fun(a,b,a,k);  
        printf("%d",a[k-1]);  
        for(i = k -2 ;i>= 0 ;--i)  
        printf(" %d",a[i]);  
        printf("\n");  
    }  

}

