using namespace std;  
char mp[55][55];  
int vis[55][55];  
int n,m;  
int x,y,ex,ey;  
int ans;  
int dx[4]={0,1,-1,0};  
int dy[4]={1,0,0,-1};  
struct node   
{  
    int x,y,step;  
    bool friend operator < (node a,node b)  
    {  
        return a.step>b.step;  
    }   
}a,temp;  
int judge()  
{  
    if(temp.x<1||temp.x>n)    return 0;  
    if(temp.y<1||temp.y>m)    return 0;  
    if(vis[temp.x][temp.y]==1)  return 0;  
    if(temp.step>=ans) return 0;  
    return 1;  
}  
void bfs()  
{  
    a.x=x;  
    a.y=y;  
    a.step=0;  
    priority_queue<node>q;  
    q.push(a);  
    memset(vis,0,sizeof(vis));  
    vis[x][y]=1;  
    while(!q.empty())  
    {  
        a=q.top();  
        q.pop();  
        for(int i=0;i<4;i++)  
        {  
            temp.x=a.x+dx[i];  
            temp.y=a.y+dy[i];  
            if(mp[temp.x][temp.y]=='#')  
                temp.step=a.step+4;  
            else  
                temp.step=a.step+1;  
            if(judge())  
            {  
                if(temp.x==ex&&temp.y==ey)  
                {  
                    ans=temp.step;  
                    return;  
                }  
                q.push(temp);  
                vis[temp.x][temp.y]=1;  
            }  
        }  
    }  
}  
