{  
    while(~scanf("%d%d",&n,&m))  
    {  
        ans=INF; 
        for(int i=1;i<=n;i++)  
        {  
            scanf("%s",mp[i]+1);      
        }  
        x=1;y=1;  
        scanf("%d%d",&ex,&ey);  
        if(ex==x&&ey==y)  
        {  
            printf("0\n");  
            continue;  
        }  
        bfs();  
        printf("%d\n",ans);  
    }  
    return 0;  
}

