#include <iostream>  
#include <cstdio>  
typedef long long LL;  
LL Cal_Mul(LL a,LL b,LL m)   
{  
    LL res(0),base(a);  
    while(b)  
    {  
        if(b&1) res=(res+base)%m;  
        base=(base+base)%m;  
        b>>=1;  
    }  
    return res;  
}  
LL Cal_Pow(LL a,LL b,LL m)  
{  
    LL res(1),base(a);  
    while(b)  
    {  
        if(b&1) res=Cal_Mul(res,base,m);  
        base=Cal_Mul(base,base,m);  
        b>>=1;  
    }  
    return res;  
}  
LL Solve(LL a,LL n,LL m)    
{  
    if(n==1) return a%m;  
    LL res=Solve(a,n/2,m);  
    if(n&1)  
    {  
        LL temp=Cal_Pow(a,n/2+1,m);  
        return (res+temp+Cal_Mul(res,temp,m))%m;  
    }  
    else  
    {  
        LL temp=Cal_Pow(a,n/2,m);  
        return (res+Cal_Mul(res,temp,m))%m;  
    }  
}  
int main()  
{  
    LL a,m,n;  
    while(~scanf("%lld%lld%lld",&a,&n,&m))  
        printf("%lld\n",Solve(a,n,m));  
    return 0;  
}

