long long gcd(long a,long b)  
{  
    return b==0 ? a : gcd(b,a%b);  
}  
long long LCM(long a,long b) 
{  
    return (a*b)/gcd(a,b);  
}  

void dfs(int deep,int a,long long lcm,int m)  
{  
    if(deep > m)
    {  
        if(a == 0) 
            return;  
        if(a%2==1)  
            ans += n/lcm;  
