int min(int a,int b)  
{  
    return a>b?b:a;  
}  
int DP(int u)  
{  
    vex[u]=1;  
    int i,j,k,v,len=adj[u].size();  
    for(i=0;i<len;i++)  
    {  
        v=adj[u][i];  
        vex[u]+=DP(v);  
    }  
    f[u][1]=len;  
    for(i=0;i<len;i++)  
    {  
        v=adj[u][i];  
        for(k=vex[u];k>=1;k--)  
        {  
            for(j=1;(j<k)&&(j<=vex[v]);j++)  
                f[u][k]=min(f[u][k],f[u][k-j]+f[v][j]-1);  
        }  
    }  
    if(vex[u]>=p)  
        ans=min(ans,f[u][p]+(u!=1));  
    return vex[u];  
}  
