          int x;  
          for (int i = 1; i <= p; ++ i)  
               scanf("%d",&x),T.add(0,x,INS);  

          for (int i = 1; i <= m; ++ i)  
              {  
              int u,v;  
              scanf("%d%d",&u,&v);       
              T.add(u,v,1);  
              T.add(v,u,1);  
              }  

          T.add(n,N,INS);  

          printf("Case #%d: %d\n",++cnt,T.GetMaxFlow(0,N));  
          }  
