const int N = 1005;  
int a[N], Max, sum, n, m;  

bool judge(int x)  
{  
    int s = 0, cnt = 0;  
    for(int i = 0; i < n; i++)  
    {  
        if(a[i] > x)  
            return false;  
        if(s + a[i] > x)  
        {  
            cnt++;  
            s = a[i];  
            if(cnt > m - 1)  
                return false;  
        }  
        else  
            s += a[i];  
    }  
    return true;  
}  

int get_ans()  
{  
    int l = Max, r = sum;  
    while(l <= r)  
    {  
        int mid = (l + r) / 2;  
        if(judge(mid))  
            r = mid - 1;  
        else  
            l = mid + 1;  
    }  
    return l;  
}  

