    {  
        sum = 0, Max = 0;  
        for(int i = 0; i < n; i++)  
        {  
            scanf("%d",&a[i]);  
            sum += a[i];  
            Max = max(Max, a[i]);  
        }  
        printf("%d\n",get_ans());  
    }  
    return 0;  
}

