#include<stdlib.h>   
struct node  
{  
    int cnt;  
    struct node *next[80]; 
};  
struct node *root;
struct node* build()  
{   
    struct node *p=(node *)malloc(sizeof(node));  
    p->cnt=0;   
    for(int i=0;i<80;i++)  
    {  
        p->next[i]=NULL;
    }  
    return p;  
}  
int insert(char*s)  
{  
    struct node *p=root;  
    int len=strlen(s);  
    for(int i=0;i<len;i++)  
    {  
        if(p->next[s[i]-'+']!=NULL)  
             p=p->next[s[i]-'+'];  
        else  
        {  
            p->next[s[i]-'+']=build();  
            p=p->next[s[i]-'+'];  
        }  
    }  
    return p->cnt++;  
}  
int search(char *s)  
{  
    int len=strlen(s);  
    struct node *p=root;  
    for(int i=0;i<len;i++)  
    {  
        if(p->next[s[i]-'+']!=NULL)  
           p=p->next[s[i]-'+'];  
        else return 0;  
    }  
    return p->cnt;  
}  
