#include<iostream>
#include<cstdio>
#include<cstring>
#include<algorithm>
#include<cmath>
#define N 1505
using namespace std;
struct node
{
 int x,y;
} digt[N];
int xx[N];
int yy[N];
int lx,ly;
int n,k;
int getx(int x)
{
   return int(lower_bound(xx,xx+lx,x)-xx);
}
int gety(int y)
{
    return int(lower_bound(yy,yy+ly,y)-yy);
}
int dp[N][N];
void init()
{
    memset(dp,0,sizeof(dp));
    for(int i=0; i<n; i++)
    {
        int x1=getx(digt[i].x);
        int y1=gety(digt[i].y);
        dp[x1][y1]++;
    }

    for(int i=1; i<ly; i++)
        dp[0][i]=dp[0][i-1]+dp[0][i];
    for(int i=1; i<lx; i++)
        dp[i][0]=dp[i-1][0]+dp[i][0];
    for(int i=1; i<lx; i++)
        for(int j=1; j<ly; j++)
            dp[i][j]=dp[i-1][j]+dp[i][j-1]-dp[i-1][j-1]+dp[i][j];
}
int nextx[N],nexty[N];
bool solve(int len)
{
    int t=0;
    for(int i=0;i<lx;i++)
    {
        for(;t<lx;t++)
            if(xx[i]+len<xx[t])break;

        nextx[i]=t-1;
    }
    t=0;
    for(int i=0;i<lx;i++)
    {
        for(;t<lx;t++)
            if(yy[i]+len<yy[t])break;
        nexty[i]=t-1;
    }

    int x1,y1;
    for(int i=0;i<lx;i++)
        for(int j=0;j<ly;j++)
        {
            x1=nextx[i];
            y1=nexty[j];

            int ll=0,rr=0,lr=0;
            if(i>0)ll=dp[i-1][y1];
            if(j>0)rr=dp[x1][j-1];
            if(i>0&&j>0)
                lr=dp[i-1][j-1];

            if(dp[x1][y1]-ll-rr+lr>=k)return true;

        }
    return false;
}
int main()
{
    int test=1;
    while(scanf("%d%d",&n,&k)!=EOF)
    {

        for(int i=0; i<n; i++)
        {
            scanf("%d%d",&digt[i].x,&digt[i].y);
            xx[i]=digt[i].x;
            yy[i]=digt[i].y;
        }
        sort(xx,xx+n);
        sort(yy,yy+n);
        lx=unique(xx,xx+n)-xx;
        ly=unique(yy,yy+n)-yy;
        init();
        int l=0,r=2000005;
        while(l<r)
        {
            int  mid=(l+r)>>1;
            if(solve(mid))r=mid;
            else l=mid+1;
        }
        printf("Case %d: %d\n",test++,l);
    }
    return 0;
}

#include<stdio.h>  
#include<math.h>  
#include<string.h>  
#include<algorithm>  
using namespace std;  
typedef long long LL;  
const LL mod = 20090531;  
LL x,y;  
LL euler(LL p)
{  
    LL res = p;  
    for(LL i = 2;i <= sqrt(p);i++)  
    {  
        if(p%i==0)  
        {  
            while(p%i==0) p/=i;  
            res = res/i*(i-1);  
        }  
    }  
    if(p > 1) res = res / p * (p-1);  
    return res%mod;  
}  
LL fast_pow(LL a,LL b) 
{  
    LL res = 1;  
    while(b)  
    {  
        if(b&1) res = (res * a) % mod;  
        a = (a * a) % mod;  
        b >>= 1;  
    }  
    return res;  
}  
void edgcd(LL a,LL b) 
{  
    if(b == 0)  
    {x = 1;y = 0;return;}  
    edgcd(b,a%b);  
    LL temp = x;  
    x = y;y = temp - a/b*y;  
}  
void solve(LL m)  
{  
    LL res = 0;  
    for(LL i = 1;i <= sqrt(m);i++)  
    {  
        if(m%i==0)  
        {  
            if(m/i != i) res = (res + euler(i)*fast_pow(m,m/i-1))%mod;  
            res = (res + euler(m/i)*fast_pow(m,i-1))%mod;  
        }  
    }  
    if(m&1)  
    {  
        res = (fast_pow(m,(m+1)/2) + res) % mod;  
        edgcd(2,mod);  
    }  
    else  
    {  
        res = (fast_pow(m,m/2+1) + fast_pow(m,m/2) + 2 * res) % mod;  
        edgcd(4,mod);  
    }  
    while(x < 0) x = (x + mod) % mod;  
    printf("%lld\n",x * res % mod);  
}  
int main()  
{  
    int n;  
    LL m;  
    scanf("%d",&n);  
    while(n--)  
    {  
        scanf("%lld",&m);  
        solve(m);  
    }  
}

#include<stdio.h>  
#include<string.h>  
#include<algorithm>  
#include<queue>  
using namespace std;  
const int N = 1010;  
int step[N][N];  
int que[N*N][2];  
int head, tail;  

void bfs(int n)  
{  
    int x, y, nx, ny;  
    memset(step, 0, sizeof(step));  
    step[0][1] = 0;  
    que[0][0] = 0;  
    que[0][1] = 1;  
    head = 0;  
    tail = 1;  
    while(head < tail)  
    {  
        x = que[head][0];  
        y = que[head++][1];  
        if((x == n - 1 && y  == n - 2) || (x == n - 2 && y == n - 1))  
            return ;  
        int mmax = max(x, y);  
        int mmin = min(x, y);  
        x = mmin;  
        y = mmax;  
        if(x - 1 > 0) 
        {  
            nx = x - 1;  
            ny = y;  
            if(!step[nx][ny])  
            {  
                step[nx][ny] = step[x][y] + 1;  
                que[tail][0] = nx;  
                que[tail++][1] = ny;  
            }  
        }  
        if(x + 1 < n && x + 1 != y)  
        {  
            nx = x + 1;  
            ny = y;  
            if(!step[nx][ny])  
            {  
                step[nx][ny] = step[x][y] + 1;  
                que[tail][0] = nx;  
                que[tail++][1] = ny;  
            }  
        }  
        if(y - 1 > 0 && y - 1 != x)   
        {  
            nx = x;  
            ny = y - 1;  
            if(!step[nx][ny])  
            {  
                step[nx][ny] = step[x][y] + 1;  
                que[tail][0] = nx;  
                que[tail++][1] = ny;  
            }  
        }  
        if(y + 1 < n)  
        {  
            nx = x;  
            ny = y + 1;  
            if(!step[nx][ny])  
            {  
                step[nx][ny] = step[x][y] + 1;  
                que[tail][0] = nx;  
                que[tail++][1] = ny;  
            }  
        }  
        if(y * 2 - x < n) 
        {  
            nx = y;  
            ny = y * 2 - x;  
            if(!step[nx][ny])  
            {  
                step[nx][ny] = step[x][y] + 1;  
                que[tail][0] = nx;  
                que[tail++][1] = ny;  
            }  
        }  
        if(x * 2 - y >= 0) 
        {  
            nx = x * 2 - y;  
            ny = x;  
            if(!step[nx][ny])  
            {  
                step[nx][ny] = step[x][y] + 1;  
                que[tail][0] = nx;  
                que[tail++][1] = ny;  
            }  
        }  
    }  
}  

int main()  
{  
    int T, n;  
    bfs(1005);  
    scanf("%d",&T);  
    while(T--)  
    {  
        scanf("%d",&n);  
        if(n == 2)  
            printf("0\n");  
        else  
            printf("%d\n", step[n-2][n-1]);  
    }  
    return 0;  
}

#include<stdio.h>  
#include<string.h>  
#include<queue>  
#include<vector>  
#include<algorithm>  
using namespace std;  
const int  maxn = 5000;  
const int inf = 0x3f3f3f3f;  
struct Edge  
{  
    int from, to, cap, flow, cost;  
    Edge(int u, int v, int c, int f, int s)  
        : from(u), to(v), cap(c), flow(f), cost(s) {}  
};  
struct MCMF  
{  
    int n, m, s, t;  
    vector<int> G[maxn];  
    vector<Edge> edges;  
    bool inq[maxn];  
    int d[maxn], p[maxn], a[maxn];  
    void inin(int n)  
    {  
        this->n = n;  
        for(int i = 0; i <= n; i++)  
            G[i].clear();  
        edges.clear();  
    }  
    void AddEdge(int from, int to, int cap, int cost)  
    {  
        edges.push_back(Edge(from, to, cap, 0, cost));  
        edges.push_back(Edge(to, from, 0, 0, -cost));  
        m = edges.size();  
        G[from].push_back(m-2);  
        G[to].push_back(m-1);  
    }  
    bool spfa(int& flow, int& cost)  
    {  
        memset(inq, false, sizeof(inq));  
        memset(d, 0x3f, sizeof(d));  
        d[s] = 0, p[s] = 0, a[s] = inf;  
        queue<int> Q;  
        Q.push(s);  
        inq[s] = true;  
        while(!Q.empty())  
        {  
            int x = Q.front();  
            Q.pop();  
            inq[x] = false;  
            for(int i = 0; i < G[x].size(); i++)  
            {  
                Edge& e = edges[G[x][i]];  
                if(e.cap>e.flow && d[e.to]>d[x]+e.cost)  
                {  
                    d[e.to] = d[x] + e.cost;  
                    p[e.to] = G[x][i];  
                    a[e.to] = min(a[x], e.cap-e.flow);  
                    if(!inq[e.to])  
                    {  
                        Q.push(e.to);  
                        inq[e.to] = true;  
                    }  
                }  
            }  
        }  
        if(d[t] == inf)  
            return false;  
        flow += a[t];  
        cost += d[t] * a[t];  
        int u = t;  
        while(u != s)  
        {  
            edges[p[u]].flow += a[t];  
            edges[p[u]^1].flow -= a[t];  
            u = edges[p[u]].from;  
        }  
        return true;  
    }  
    int min_cost(int s, int t)  
    {  
        this->s = s;  
        this->t = t;  
        int flow = 0, cost = 0;  
        while(spfa(flow, cost));  
        return cost;  
    }  
};  
int main()  
{  
    MCMF solve;  
    int T, m, n;  
    scanf("%d", &T);  
    while(T--)  
    {  
        scanf("%d%d", &m, &n);  
        int k = n/m+1;  
        int s = 0, t = 2*n+1;  
        solve.inin(t);  
        for(int i = 1; i <= n; i++)  
            solve.AddEdge(s, i, 1, 0);  
        for(int i =  1; i <= n; i++)  
        {  
            for(int j = 1; j <= n; j++)  
            {  
                int a;  
                scanf("%d", &a);  
                if(j <= k) 
                solve.AddEdge(i, j+n, 1, a);  
            }  
            solve.AddEdge(i+n, t, m, 0);  
        }  
        printf("%d\n", solve.min_cost(s, t));  
    }  
    return 0;  
}

