#include<stdio.h>  
#include<algorithm>  
#define INF 0x3fffffff  
using namespace std;  

const int N = 5005;  
struct edge  
{  
    int x;  
    int y;  
    int v;  
}e[N];  
int father[N];  

void Init(int n)  
{  
    for(int i = 1; i <= n; i++)  
        father[i] = i;  
}  

bool comp(edge e1, edge e2)  
{  
    return e1.v > e2.v;  
}  

int Find(int x)  
{  
    if(x != father[x])  
        father[x] = Find(father[x]);  
    return father[x];  
}  

void Union(int a, int b)  
{  
    int p = Find(a);  
    int q = Find(b);  
    if(p > q)  
        father[p] = q;  
    else  
        father[q] = p;  
}  

int gcd(int a, int b)  
{  
    while(b != 0)  
    {  
        int r = a % b;  
        a = b;  
        b = r;  
    }  
    return a;  
}  

int main()  
{  
    int T, n, m, i, j, s, t, aa, bb;  
    scanf("%d",&T);  
    while(T--)  
    {  
        scanf("%d%d",&n,&m);  
        for(i = 0; i < m; i++)  
            scanf("%d%d%d",&e[i].x, &e[i].y, &e[i].v);  
        scanf("%d%d",&s,&t);  
        sort(e, e+m, comp);  
        double rate = INF*1.0;  
        for(i = 0; i < m; i++)  
        {  
            Init(n);  
            for(j = i; j < m; j++) 
            {  
                if(Find(e[j].x) != Find(e[j].y))  
                    Union(e[j].x, e[j].y);  
                if(Find(s) == Find(t))  
                    break;  
            }  
            if(j == m) break;  
            int v1 = e[i].v, v2 = e[j].v;  
            if(v1*1.0 / v2 < rate) 
            {  
                aa = v1, bb = v2; 
                rate = v1*1.0 / v2;  
            }  
        }  
        if(rate == INF)  
            printf("IMPOSSIBLE\n");  
        else if(aa % bb == 0)  
            printf("%d\n",aa/bb);  
        else  
            printf("%d/%d\n",aa/gcd(aa,bb), bb/gcd(aa,bb));  
    }  
    return 0;  
}

