    if(visrow[i][k] || viscol[j][k] || vissqu[t][k]) return 0;  
    return 1;  
}  

void DFS(int k){  
    if(k == count){ ok = 1; print(); return; }  
    int t = getsqunum(tar[k].x, tar[k].y);  
    for(int i = 1; i < 10; ++i){  
