matrix Pow(matrix& src, lld k){
	if(k == 1){
		return src;
	}	
	matrix base= Pow(src,k>>1);
	matrix exp= Mul(base,base);
	if(k&1){
		return Mul(exp,src);
	}else{
		return exp;
	}
}
matrix Mul(matrix& lhs, matrix& rhs){
	matrix result= {lhs.x11*rhs.x11+lhs.x12*rhs.x21,
	lhs.x11*rhs.x12+lhs.x12*rhs.x22,
	lhs.x21*rhs.x11+lhs.x22*rhs.x21,
	lhs.x21*rhs.x12+lhs.x22*rhs.x22};
	result.x11%=m;
	result.x12%=m;
	result.x21%=m;
	result.x22%=m;
	return result;
}

