bool Judge(int a){  
    int i = 0;  
    while(a){  
        str[i++] = a % 10 + '0';  
        if(str[i-1] > '6') return 0;  
        a /= 10;  
    }  
    while(i < 6) str[i++] = '0';  
    str[i] = '\0';  
    for(i = 1; i != 6; ++i){  
        if(str[i] == str[i+1] && str[i] == str[i-1]) return 0;  
        if(abs(str[i] - str[i-1]) > 4) return 0;  
    }  
    return 1;  
}  

