#include <cstdio>  

int fac[20];  
void Div(int n) {  
    int k = 0;  
    for(int i = 2; i * i <= n; ++i){  
        if(n % i == 0) fac[++k] = i;  
        while(n % i == 0) n /= i;  
    }  
    if(n > 1) fac[++k] = n;  
    fac[0] = k;  
}  
int que[1<<10];  
int Count(int n, int m) {  
    int g = 0, sum = n;  
    que[++g] = 1;  
    for(int i = 1; i <= fac[0]; ++i){  
        int t = g;  
        for(int j = 1; j <= g; ++j){  
            que[++t] = que[j] * fac[i] * -1;  
            sum += n / que[t];  
        }  
        g = t;  
    }  
    return sum;  
}   
int Binary_search(int m, int K){  
    int l = 1, r = 2000000000, mid;  
    while(l <= r){  
        mid = (l + r) >> 1;  
        if(Count(mid, m) >= K) r = mid - 1;  
        else l = mid + 1;  
    }  
    return l;  
}  
int main()  
{  
    int m, K;  
    while(scanf("%d%d", &m, &K) != EOF)  
    {  
        Div(m);  
        int ans = Binary_search(m, K);  
        printf("%d\n", ans);  
    }  
    return 0;  
}

