#include <stdlib.h>  
#include <vector>  
#include <queue>  
#define Max_N 1100  
#define inf 0x3f3f3f3f  
using namespace std;  
struct Edge{  
    int to,flow,rev;  
};  
int n;  
int v[Max_N];  
int dp[Max_N];  
int level[Max_N];  
int iter[Max_N];  
vector<Edge> g[Max_N];  
void init(){  
    memset(dp,0,sizeof(dp));  
    for(int i=0;i<Max_N;i++)  
        g[i].clear();  
}  
int find_max(int a,int b){  
    return a>b?a:b;  
}  
int find_min(int a,int b){  
    return a>b?b:a;  
}  
void add_edge(int from,int to,int cap){  
    g[from].push_back((Edge){to,cap,g[to].size()});  
    g[to].push_back((Edge){from,0,g[from].size()-1});  
}  
void bfs(int s)  
{  
    memset(level,-1,sizeof(level));  
    queue<int> que;  
    level[s]=0;  
    que.push(s);  
    while(!que.empty())  
    {  
        int v=que.front();que.pop();  
        for(int i=0;i<g[v].size();i++)  
        {  
            Edge &e=g[v][i];  
            if(e.flow>0&&level[e.to]<0)  
            {  
                level[e.to]=level[v]+1;  
                que.push(e.to);  
            }  
        }  
    }  
}  
int dfs(int v,int t,int f)  
{  
    if(v==t) return f;  
    for(int &i=iter[v];i<g[v].size();i++)  
    {  
        Edge &e=g[v][i];  
        if(e.flow>0&&level[v]<level[e.to])  
        {  
            int d=dfs(e.to,t,find_min(f,e.flow));  
            if(d>0)  
            {  
                e.flow-=d;  
                g[e.to][e.rev].flow+=d;  
                return d;  
            }  
        }  
    }  
    return 0;  
}  
int max_flow(int s,int t)  
{  
    int flow=0;  
    while(true)  
    {  
        bfs(s);  
        if(level[t]<0) return flow;  
        memset(iter,0,sizeof(iter));  
        int f;  
        while((f=dfs(s,t,inf))>0)  
            flow+=f;  
    }  
    return 0;  
}  
int main()  
{  
    int i,j;  
    while(~scanf("%d",&n))  
    {  
        init();  
        int len=0;  
        int S=0,T=2*n+1;  
        for(i=1;i<=n;i++)  
        {  
            scanf("%d",&v[i]);  
            for(j=i-1;j>0;j--)  
                if(v[i]>v[j])  
                    dp[i]=find_max(dp[i],dp[j]+1);  
            len=find_max(len,dp[i]);  
            for(j=i-1;j>0;j--)  
            {  
                if(v[i]>v[j]&&dp[i]-dp[j]==1)  
                {  
                    add_edge(j*2,i*2-1,1);  
                }  
            }  
            add_edge(i*2-1,i*2,1);  
        }  
        for(i=1;i<=n;i++)  
        {  
            if(dp[i]==0)  
                add_edge(S,i*2-1,1);  
            if(dp[i]==len)  
                add_edge(i*2,T,1);  
        }  
        ++len;  
        int max=max_flow(S,T);  
        printf("%d\n",len);  
        printf("%d\n",max);  
    }  
    return 0;  
}

