int arr[32];
int ok;

int intToString(int n, int jinzhi){
	int i = 0;
	while(n){
		arr[i++] = n % jinzhi;
		n /= jinzhi;
	}
	int left = 0, right = i - 1;
	while(left < right){
		if(arr[left++] != arr[right--])
			return 0;
	}
	return 1;
}

void proceed(int n){
	int i;
	for(i = 2; i <= 16; ++i){
		if(intToString(n, i)){
			if(!ok){
				printf("number %d is palindrom in basis", n);
				ok = 1;
			}
			printf(" %d", i);
		}
	}
}

