#include <stdio.h>  
#include <queue>  
#include <string.h>  
#include <algorithm>  
using namespace std;  
struct node  
{  
    char ch,code[100];  
    int val,left,right,fa,num;  
    friend bool operator<(node a, node b)  
    {  
        if(a.val>b.val) return true;  
        if(a.val==b.val&&a.ch>b.ch) return true;  
        return false;  
    }  
}tree[200];  
int N;    
char str[100];   
bool cmp(node a,node b)  
{  
    return a.num<b.num;  
}  
void dfs(int t,int q)  
{  
    if(t<N)  
    {    
        str[q]='\0';  
        strcpy(tree[t].code,str);   
        return ;  
    } 
    else  
    {  
        if(tree[t].left!=-1)  
        {  
            str[q]='0';  
            q++;  
            dfs(tree[t].left,q);   
            q--;  
        }
        if(tree[t].right!=-1)  
        {  
            str[q]='1';  
            q++;  
            dfs(tree[t].right,q);  
            q--;  
        }
    } 
}
int main()  
{  
    priority_queue<node>s;  
    int n;  
    while(scanf("%d",&n)!=EOF)  
    {  
        if(n==0)  
        continue;   
        memset(str,0,sizeof(str));  
        memset(tree,0,sizeof(tree));   
        for(int i=0;i<n;i++)  
        {  
            getchar();  
            node temp;  
            scanf("%c %d",&temp.ch,&temp.val);  
            temp.left=temp.right=temp.fa=-1;  
            temp.num=i;  
            s.push(temp);  
        }  
        N=n;  
        int t=0;   
        while(s.size()>1)  
        {  
            node temp1,temp2,temp3;  
            temp1=s.top();s.pop();  
            temp2=s.top();s.pop();  
            temp3.val=temp1.val+temp2.val;  
            temp3.ch=temp1.ch;  
            temp3.left=temp1.num;  
            temp3.right=temp2.num;  
            temp3.fa=0;  
            temp1.fa=temp2.fa=temp3.num=n++;  
            tree[t++]=temp1;  
            tree[t++]=temp2;  
            s.push(temp3);  
        }     
        tree[t++]=s.top();  
        s.pop();  
        sort(tree,tree+t,cmp);   
        dfs(t-1,0);   
