int insert(int dp[],int k,int x)  
{  
    int i;  
    for(i=k;i>=0;i--)  
    {  
        if(dp[i]<x)  
        {  
            dp[i+1]=x;  
            if(k==i)  
            {  
                return 1;  
            }  
            else  
            {  
                return 0;  
            }  
        }  
    }  
}  
int main()  
{  
    int n,i,j,head,tail;  
