    dp1[0]=-0xffffff;dp2[0]=-0xffffff;  
    while(scanf("%d",&n)&&n)  
    {  
        head=0;tail=0;  
        for(i=0;i<n;i++)  
            scanf("%d",&seq[i]);  
        if(n<=2)  
        {  
            printf("1\n");  
            continue;  
        }  
        for(i=0,j=n-1;i<=j;i++,j--)  
        {  
            if(i==j)  
            {  
                if(insert(dp1,head,seq[i])==1)  
                    head++;  
                break;  
            }  
            while(insert(dp1,head,seq[i])!=1)  
            {  
                i++;  
                if(i>=j)  
                    break;  
            }  
            if(i>j)  
                break;  
            else if(i<j)  
                head++;  
            else  
                i--;  
            while(insert(dp2,tail,seq[j])!=1)  
            {  
                j--;  
