            if(i>j)  
                break;  
            else if(i<j)  
                tail++;  
            else  
                j++;  
        }  
        if(dp1[head]>dp2[tail])  
        {  
            if(head>tail)  
                printf("%d\n",tail*2+1);  
            else  
                printf("%d\n",head*2-1);  
        }  
        else if(dp1[head]==dp2[tail])  
        {  
            if(head>=tail)  
                printf("%d\n",tail*2-1);  
            else  
                printf("%d\n",head*2-1);  
        }  
        else  
        {  
            if(head>=tail)  
