using namespace std;  

const int N=100;  
struct Node {  
    char  date;  
    Node *left,*right;  
    Node (){  
        left=NULL;  
        right=NULL;  
    }  
};  
void BuildBinaryTree(Node *& root,char *preorder,char *midorder,int len) {  
    if(len==0) {  
        root = NULL;
        return ;  
    }  

    root = new Node();
    root->date=preorder[0];  
    char *rootplace=strchr(midorder,root->date); 

    int leftlen=strlen(midorder)-strlen(rootplace);  
    int rightlen=len-leftlen-1;  
    BuildBinaryTree(root->left,preorder+1,midorder,leftlen);  
    BuildBinaryTree(root->right,preorder+leftlen+1,rootplace+1,rightlen);  
}  

void PostOrder(Node *root){  
    if(root==NULL) return ;  
    PostOrder(root->left);  
    PostOrder(root->right);  
    cout<<root->date;  
}  

int main(){  
    char preorder[N],midorder[N];  
    while(cin >> preorder >> midorder){  
        Node *root=NULL;  
        BuildBinaryTree(root,preorder,midorder,strlen(preorder));  
        PostOrder(root);  
        cout << endl;  
    }  
    return 0;  
}

#include <stdio.h>  
#include <string.h>  
#include <stdlib.h>  
#include <vector>  
#define Maxsize 110  
#define INF 0x3f3f3f3f  
int n,m;  
int dist[Maxsize];  
int g[Maxsize][Maxsize];  
int mincost[Maxsize][Maxsize];  
int pre[Maxsize];  
bool used[Maxsize][Maxsize];  
bool vis[Maxsize];  
void init()  
{  
    memset(g,0x3f,sizeof(g));  
    memset(dist,0x3f,sizeof(dist));  
    memset(mincost,0x3f,sizeof(mincost));  
    memset(used,0,sizeof(used));  
    memset(vis,0,sizeof(vis));  
    for(int i=0;i<=n;i++)  
        mincost[i][i]=0;  
}  
void addedge(int a,int b,int c)  
{  
    if(c<g[a][b])  
    {  
        g[a][b]=c;  
        g[b][a]=c;  
    }  
}  
int findmin(int x,int y)  
{  
    return x>y?y:x;  
}  
void findtree()  
{  
    int u;  
    dist[1]=0;  
    pre[1]=0;  
    for(;;)  
    {  
        int min=INF;  
        for(int i=1;i<=n;++i)  
            if(min>dist[i]&&!vis[i])  
            {  
                min=dist[i];  
                u=i;  
            }  
        if(min==INF)  
            break;  
        vis[u]=1;  
        int v=pre[u];  
        used[u][v]=used[v][u]=1;  
        mincost[u][v]=mincost[v][u]=g[u][v];  
        for(v=1;v<=n;v++)  
            if(!vis[v]&&dist[v]>g[u][v])  
            {  
                pre[v]=u;  
                dist[v]=g[u][v];  
            }  
    }  
}  
void findmincostload()  
{  
    for(int k=1;k<=n;k++)  
        for(int i=1;i<=n;i++)  
            for(int j=1;j<=n;j++)  
                mincost[i][j]=findmin(mincost[i][j],mincost[i][k]+mincost[k][j]);   
}  
int solve()  
{  
    int res=INF;  
    findtree();  
    findmincostload();  
    for(int i=1;i<=n;i++)  
        for(int j=i+1;j<=n;j++)  
            if(used[i][j]==0)  
                res=findmin(res,mincost[i][j]+g[i][j]);  
    return res;  
}  
int main()  
{  
    while(~scanf("%d%d",&n,&m))  
    {  
        init();  
        int a,b,c;  
        for(int i=0;i<m;++i)  
        {  
            scanf("%d%d%d",&a,&b,&c);  
            addedge(a,b,c);  
        }  
        int t=solve();  
        if(t==INF)  
