using namespace std;  
typedef long long LL;  
LL fib[100];  
void get_fib()  
{  
    fib[0] = 1; fib[1] = 1;  
    for(int i = 2; i <= 90; i++)  
        fib[i] = fib[i-1] + fib[i-2];  
}  
int main()  
{  
    get_fib();  
    LL x, y, m;  
    while(~scanf("%lld%lld%lld",&x, &y, &m))  
    {  
        LL temp, i, sum = 0;  
        if(x >= m || y >= m)  
        {  
            printf("0\n");  
            continue;  
        }  
        if(x > y) swap(x,y);  
        if(x <= 0 && y <= 0)  
        {  
            if(y >= m)  printf("0\n");  
            else  printf("-1\n");  
            continue;  
        }  
        if(x < 0 && y > 0)  
        {  
            sum = (-x) / y + 1;  
            x += sum * y;  
        }
        if(x > y) swap(x,y);  
        for(i = 1; i <= 90; i++)  
        {  
            temp = fib[i]*y + fib[i-1]*x;  
            if(temp >= m)  
                break;  
        }  
        printf("%lld\n",sum + i);  
    }  
    return 0;  
}

