    for(int i=3;i<22;i++)  
        a[i]=pow(10,i-1)+8;  
}  
string to_string(long long x)  
{  
    string ans;  
    while(x)  
    {  
        ans+=(x%10+'0');  
        x/=10;  
    }  
    reverse(ans.begin(),ans.end());  
    return ans;  
}  
long double solve(string x,long long y)    
{  
    if(x.size()<=2)  
        return b[y];  
    long double ans=0;  
    ans+=a[x.size()-1];  
    ans+=(x[0]-'0'-1)*pow(10,(x.size()-2));  
    long long cmp=0;  
    for(int i=1;i<x.size()-1;i++)  
        cmp=cmp*10+(x[i]-'0');  
    ans+=cmp;  
    if(x[x.size()-1]>=x[0])  
        ans++;  
    return ans;  
}  
void isit()  
{  
    b[0]=0;  
    memset(b,0,sizeof(b));  
    for(int i=1;i<=100;i++)  
    {  
        int en=i%10,k=i/10,wei=1;  
        while(k)  
        {  
            k/=10;  
            wei*=10;  
        }  
        if(i/wei==en)  
            b[i]=b[i-1]+1;  
        else  
            b[i]=b[i-1];  
    }  
}  

int main()  
{  
    long long x,y;  
    isit();  
    read();  
    while(~scanf("%lld%lld",&x,&y))  
    {  
        string st,se;  
        st=to_string(x-1),se=to_string(y);  
        long double ans_x=solve(st,x-1),ans_y=solve(se,y);  
        double sum=(double)(ans_y-ans_x)/(double)(y-x+1);  
        printf("%.3lf\n",sum);  
    }  
    return 0;  
}

