int Binary_search(int l, int r, int k)  
{  
    while(l < r)  
    {  
        int mid = (l + r) / 2;  
        if(a[mid].sco > k)  
            r = mid;  
        else if(a[mid].sco < k)  
            l = mid+1;  
        else  
        {  
            if(a[mid].sco == a[mid - 1].sco)  
                r = mid;  
            else  
                return mid;  
        }  
    }  
}  
