int n, k;  

bool judge(double a)  
{  
    for(int i = 0; i < n; i++)  
        x[i] = v[i] - a * w[i];   
    sort(x, x+n);  
    double sum = 0;  
    for(int i = 0; i < k; i++)  
        sum += x[n-1-i];  
    return sum >= 0 ? true : false;  
}  

double get_ans()  
{  
    double l = 0, r = max_ave;  
    while(r - l > eps)  
    {  
        double mid = (l + r) / 2;  
        if(judge(mid))  
            l = mid;  
        else  
            r = mid;  
    }  
    return l;  
}  

