#include<cstdio>  
#include<algorithm>  
#include<vector>  
using namespace std;  
const int N = 5e2 + 10;  
int father[N], deg[N];  
vector<int> vec[N];  

void Initial(int n) 
{  
    for(int i = 1; i <= n; i++)  
        father[i] = i, deg[i] = 0;  
}  

int Find(int x)  
{  
    if(father[x] != x)  
        father[x] = Find(father[x]);  
    return father[x];  
}  

void Union(int a, int b)  
{  
    int p = Find(a);  
    int q = Find(b);  
    if(p != q)  
        father[p] = q;  
}  

int main()  
{  
    int n, m, i, j, cas = 1;  
    while(~scanf("%d%d",&n,&m) && (n+m))  
    {  
        Initial(n); 
        int u, v;  
        for(i = 0; i < m; i++)  
        {  
            scanf("%d%d",&u,&v);  
            deg[u]++;  
            deg[v]++;  
            Union(u,v);  
        }  
        for(i = 1; i <= n; i++)  
        {  
            vec[i].clear(); 
            if(Find(i) == i)  
            {  
                for(j = 1; j <= n; j++)  
                {  
                    if(Find(j) == Find(i))  
                        vec[i].push_back(j);  
                }  
            }  
        }
        int ans = 0;  
        for(i = 1; i <= n; i++)  
        {  
            int cnt = vec[i].size();  
            if(cnt == 0)  
                continue;  
            int sum = 0;  
            for(j = 0; j < cnt; j++)  
            {  
                sum += deg[vec[i][j]];  
            } 
            if(sum == cnt * 2 - 2)  
                ans++;  
        }  
        printf("Case %d: ",cas++);  
        if(ans == 0)  
            printf("No Trees.\n");  
        else if(ans == 1)  
            printf("There is one tree.\n");  
        else  
            printf("A forest of %d trees.\n",ans);  
    }  
    return 0;  
}

