#include <cstdio>  
#include <cmath>  
const int mod = 9901;  

int Pow(int a, int n) { 
    int res = 1;  
    a %= mod;  
    while(n) {  
        if(n&1) res = res * a % mod;  
        a = a * a % mod;  
        n >>= 1;  
    }  
    return res;  
}  

int get_sum(int a, int n) {  
    if(n == 0) return 1;  
    if(n&1) return get_sum(a, n / 2) * (Pow(a, (n / 2 + 1)) + 1) % mod;  
    else return (get_sum(a, n / 2 - 1) * (Pow(a, n / 2) + 1) + Pow(a, n)) % mod;  
}  

int main() {  
    int a, b;  
    while(~scanf("%d%d", &a, &b)) {  
        int ans = 1;  
        int m = (int)sqrt(a + 0.5);  
        for(int i = 2; i <= m; ++i) {  
            if(a % i == 0) {  
                int k = 0;  
                while(a % i == 0) {  
                    k++;  
                    a /= i;  
                }  
                ans = ans * get_sum(i, k * b) % mod;  
            }  
        }  
        if(a > 1) ans = ans * get_sum(a, b) % mod;  
        printf("%d\n", ans);  
    }  
    return 0;  
}

