bool bfs()  
{  
    queue<int> q;  
    q.push(0);  
    while(!q.empty())  
    {  
        int cur = q.front();q.pop();  
        for(int i=0;i<m;i++)  
        {  
            int nxt = (cur * c + a[i]) % n;  
            if(cur == 0 && a[i] == 0 || vis[nxt])  
                       continue;  
            vis[nxt] = true;  
            ansAt[nxt] = a[i];  
            pre[nxt] = cur;  
            if(nxt == 0)  
                      return true;  
            q.push(nxt);  
        }  
    }  
    return false;  
}  

