int intcmp(int *a,int *b)  
{
    int len1=0;  
    int len2=0;  
    for(len1=0;a[len1]!=-1;len1++);  
    for(len2=0;b[len2]!=-1;len2++);  
    for(int i=len1-1,j=len2-1;i>=0&&j>=0;i--,j--)  
    {  
        if(a[i]<b[j])  
            return 1;  
        else if(a[i]>b[i])  
            return -1;  
    }  
    return 0;  
}  
