{  
    int i,j,k;  
    for(i=1;i<=n;i++)  
        for(j=1;j<=n;j++)  
        {  
            pre[i][j]=i;  
            if(g[i][j]==-1)  
            {  
                g[i][j]=inf;  
            }  
        }  
    for(k=1;k<=n;k++)  
        for(i=1;i<=n;i++)  
            for(j=1;j<=n;j++)  
            {
                if(g[i][j]>g[i][k]+g[k][j]+cost[k])  
                {  
                    g[i][j]=g[i][k]+g[k][j]+cost[k];  
                    int p,q;  
                    p=k;q=j;  
                    do  
                    {  
                        pre[i][q]=pre[p][q];  
                        q=pre[p][q];  
                    }while(q!=p);  
                }  
                else if(g[i][j]==g[i][k]+g[k][j]+cost[k])  
                {  
                    int f=i,t=k;  
                    int loc=0;  
                    do  
                    {
