                        t=pre[f][t];  
                    }while(f!=t);  
                    s1[loc]=-1;  
                    int F=i,T=j;  
                    loc=0;  
                    do  
                    {
                        s2[loc++]=T;  
                        T=pre[F][T];  
                    }while(F!=T);  
                    s2[loc]=-1;  
                    if(intcmp(s1,s2)>0)  
                    {  
                        int p,q;  
