void dfs(int u,int value)  
{  
    int i,j;  
    if(u==T)  
    {  
        ans=FindMin(value,ans);  
    }  
    for(i=0;i<count;i++)  
    {  
        if(!((u>>i)&1))  
        {  
            for(j=i+1;j<count;j++)  
                if(!((u>>j)&1))  
                {  
                    int next=u;  
                    next=next|(1<<i);  
                    next=next|(1<<j);  
                    dfs(next,value+g[v[i]][v[j]]);  
                }  
        }  
    }  
}  
