            degree[u]++;degree[e]++;  
            g[u][e]=g[e][u]=FindMin(g[u][e],l);  
        }  
        for(k=1;k<=n;k++)  
            for(i=1;i<=n;i++)  
                for(j=1;j<=n;j++)  
                    g[i][j]=FindMin(g[i][j],g[i][k]+g[k][j]);  
        count=0;  
        for(i=1;i<=n;i++)  
            if(degree[i]&1)  
                v[count++]=i;  
        T=0;  
        for(i=0;i<count;i++)  
        {  
            T=(T<<1)|1;  
        }  
        ans=INF;  
        dfs(0,0);  

