void slove(int n) {
    unsigned int res = 1;
    int i, t1, t2;
    for(i = 0; i <= num && prime[i] * prime[i] <= n; ++i) {
        t1 = prime[i];
        t2 = prime[i] * prime[i];
        while(t2 / t1 == prime[i] && t2 <= n)
        {
            t1 *= prime[i];
            t2 *= prime[i];
        }
        res *= (t1 / prime[i]);
    }
    int t = upper_bound(prime, prime+num, n) - prime - 1;
    res *= p[t];
    printf("%u\n", res);
}
